customSli.indicator:
fetch dt.entity.service
| filter matchesPhrase(tags,"slo_level:high")
| fieldsAdd dt.entity.service=id
| join [
  timeseries { total=sum(dt.service.request.count) 
  ,failures=sum(dt.service.request.failure_count) }
  , by: { dt.entity.service }
  , filter: {matchesPhrase(entityAttr(dt.entity.service,"tags"),"slo_level:high")}
], on:{dt.entity.service}, kind:leftOuter
| fields timeframe=right.timeframe, interval=right.interval, sli=(((right.total[]-right.failures[])/right.total[])*(102)),
 entityName(dt.entity.service)
