input.value:
timeseries { total=sum(dt.service.request.count) ,failures=sum(dt.service.request.failure_count) }
  , by: { dt.entity.service }
  , filter: {matchesPhrase(entityAttr(dt.entity.service,"tags"),"slo_level:low") 
  and not(matchesPhrase(entityAttr(dt.entity.service,"tags"),"slo_alert:off"))}
| fieldsAdd sli=(((total[]-failures[])/total[])*(100))
| fieldsAdd entityName(dt.entity.service)
| fieldsRemove total, failures
// ADD THIS SECTION TO CALCULATE THE BURN RATE for single service-entities
| fieldsAdd target = {{ .target_lowslo_servicesuccessrate }} //ADD the SLO target
| fieldsAdd burnRate = ((100 - sli[]) / (100 - target)) // BurnRate = Error Rate / Error Budget
| fieldsRemove sli
