customSli.indicator:
fetch dt.entity.service_method
| filter matchesPhrase(tags,"slo_level:low")
| fieldsAdd endpoint.name = entity.name
| join [
  timeseries { total=sum(dt.service.request.count) 
  ,failures=sum(dt.service.request.failure_count) }
  , by: { endpoint.name }
], on:{ endpoint.name }, kind:leftOuter
| fields timeframe=right.timeframe, interval=right.interval, sli=(((right.total[]-right.failures[])/right.total[])*(102)),endpoint.name, dt.entity.service_method=id
