input.value:
fetch dt.entity.application
| filter in(tags,"slo_level:medium") and not(in(tags,"slo_alert:off"))
| fieldsAdd dt.rum.application.entity=id
| join [
   timeseries { total=sum(dt.frontend.request.count),failures=sum(dt.frontend.error.count) }
  , by: { dt.rum.application.entity }
  //, filter: {matchesPhrase(entityAttr(dt.entity.application,"tags"),"slo")}
], on:{dt.rum.application.entity}, kind:leftOuter
| fields timeframe=right.timeframe, interval=right.interval, sli=(((right.total[]-right.failures[])/right.total[])*(100)),
 entityName(dt.rum.application.entity, type: "dt.entity.application")
// ADD THIS SECTION TO CALCULATE THE BURN RATE for single service-entities
| fieldsAdd target = {{ .target_mediumslo_userexperiencesuccessrate }}  //ADD the SLO target
| fieldsAdd burnRate = ((100 - sli[]) / (100 - target)) // BurnRate = Error Rate / Error Budget
| fieldsRemove sli
